-- Create Login
CREATE LOGIN FranmerPurview FROM EXTERNAL PROVIDER;


-- Use a managed identity for DEDICATED SQL databases
CREATE USER FranmerPurview FROM EXTERNAL PROVIDER
GO

EXEC sp_addrolemember 'db_datareader', FranmerPurview
GO

-- Use a managed identity for SERVERLESS SQL databases
CREATE USER FranmerPurview FOR LOGIN FranmerPurview;
ALTER ROLE db_datareader ADD MEMBER FranmerPurview;
